const BodySystemIcon = ({
  name,
  color,
  size = "2x",
}: {
  name: string;
  color: string;
  size?: SizeProp;
}) => {
  if (customIcon(name)) {
    return (
      <SVG color={color} size={size}>
        {customIcon(name)}
      </SVG>
    );
  } else {
    return (
      <FontAwesomeIcon
        size={size}
        className={color}
        style={{ color: color }}
        icon={faIcons[name] ?? faQuestion}
      />
    );
  }
};

export const BodySystem = ({
  name = "mortality/aging",
  isSignificant = false,
  color = "grey",
  hoverColor,
  noSpacing,
  appendLabel,
  prependLabel,
  size = "2x",
  onClick,
  noMargin = false,
}: {
  isSignificant?: boolean;
  name: string;
  color?: string;
  hoverColor?: string;
  noSpacing?: boolean;
  appendLabel?: string;
  prependLabel?: string;
  size?: SizeProp;
  onClick?: (value: string) => void;
  noMargin?: boolean;
}) => {
  const label = _.capitalize(name.replace(/ phenotype/g, ""));
  const [hovered, setHovered] = useState<boolean>(false);

  const shouldUseHoverColor = !!hoverColor && hovered;
  const spanStyles = classNames({
    [styles.bodySystemNoSpacing]: noSpacing,
    [styles.bodySystem]: !noSpacing,
    [hoverColor]: !isSignificant && shouldUseHoverColor,
    [color]: !isSignificant && !shouldUseHoverColor,
    [styles.significantBodySystem]: isSignificant && !!onClick,
    [styles.noMargin]: noMargin,
  });

  return isSignificant ? (
    <span
      className={spanStyles}
      onClick={() => onClick(name)}
      data-testid="body-icon"
    >
      <BodySystemIcon name={name} color={color} size={size} />
      &nbsp;
      <span>{label}</span>
    </span>
  ) : (
    <OverlayTrigger
      placement="top"
      trigger={["hover", "focus"]}
      onToggle={(e: boolean) => {
        setHovered(e);
      }}
      overlay={
        <Tooltip>
          {prependLabel ? `${prependLabel}: ` : ""}
          {label}
          {appendLabel ? `: ${appendLabel}` : ""}
        </Tooltip>
      }
    >
      {({ ref, ...triggerHandler }) => (
        <span
          {...triggerHandler}
          ref={ref}
          className={spanStyles}
          data-testid="body-icon"
        >
          <BodySystemIcon name={name} color="currentColor" size={size} />
        </span>
      )}
    </OverlayTrigger>
  );
};

export default BodySystemIcon;
